import React, { useState, useEffect } from 'react';

function GroceryContainer() {
    const [grocery, setGrocery] = useState([]);
    const [hasGroceries, setHasGroceries] = useState(true);

    useEffect(() => {
        fetch('/api/groceries')
        .then((res) => {
            return res.json();
        })
            .then((data) => {
                data.forEach((obj) => obj.edit = false);

                setGrocery(data);

                if (!data.length) {
                    setHasGroceries(false);
                }
            });
    }, []);

    function createGrocery(event) {
        event.preventDefault();

        fetch('/api/groceries', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                item: grocery
            })
        }).then(() => {
            setGrocery([]);
        })
    }

    function inputCallback(event) {
        setGrocery(event.target.value);
        console.log(grocery)
    }


    return (
        <div className='groceryForm'>
            <form onSubmit={createGrocery}>
                <label htmlFor='groceries'>Enter Item</label><br></br>
                <input onChange={inputCallback} value={grocery} type='text' id='groceryInput' name='grocery' placeholder='Type your grocery item'></input><br></br>
