import React from 'react';
import DayJS from 'react-dayjs';

const Header = () => {

  function now(){
    setInterval(() => {
      new Date();
    }, 1000);
  }



  return (
    <header>
        <h1>Grocery List</h1>
        <DayJS format='dddd MMMM D, YYYY'>{now}</DayJS>
        <p>Current Time: <DayJS format='HH:mm:ss'>{now}</DayJS></p>
    </header>
  );
}

export default Header;
