import React, { useState } from 'react';

function GroceryContainer() {
    const [grocery, setGrocery] = useState('');

    function createGrocery(event) {
        event.preventDefault();

        fetch('/api/grocery', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                item: grocery
            })
        }).then(() => {
            setGrocery('');
        })
    }

    function inputCallback(event) {
        setGrocery(event.target.value);
    }


    return (
        <div className='groceryForm'>
            <form onSubmit={createGrocery}>
                <label htmlFor='groceries'>Enter Item</label><br></br>
                <input onChange={inputCallback} type='text' id='groceryInput' placeholder='Peanut Butter'></input><br></br>
                <button type='submit' id='submitButton' className='submitButton'>Add Item</button>
            </form>

            <ul className='groceryList'>
                <li>Peanut Butter<button id='editButton' className='editButton'>Edit</button><button id='deleteButton' className='deleteButton'>Delete</button></li>
            </ul>

        </div>
    );
}



export default GroceryContainer;
