import React, { useState } from 'react';

function GroceryContainer() {
    const [grocery, setGrocery] = useState([]);

    function createGrocery(event) {
        event.preventDefault();

        fetch('/api/groceries', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                item: grocery
            })
        }).then(() => {
            setGrocery([]);
        })
    }

    function inputCallback(event) {
        setGrocery(event.target.value);
    }

    // function displayList() {
    //     fetch('/api/groceries', (req, res) => {
    //         
    //         .then(re) => {
    //         })
    //     })
    // }


    return (
        <div className='groceryForm'>
            <form onSubmit={createGrocery}>
                <label htmlFor='groceries'>Enter Item</label><br></br>
                <input onChange={inputCallback} value={grocery} type='text' id='groceryInput' name='grocery' placeholder='Type your grocery item'></input><br></br>
                <button type='submit' id='submitButton' className='submitButton'>Add Item</button>
            </form>

            <ul className='groceryList'>
                <li>
                    <button id='editButton' className='editButton'>Edit</button>
                    <button id='deleteButton' className='deleteButton'>Delete</button>
                </li>
            </ul>

        </div>
    );
}

export default GroceryContainer;
