import React, { useState, useEffect } from 'react';
import FormContainer from './components/FormContainer';
import GroceryList from './components/GroceryList';
import Header from './components/Header';

const App = () => {
    const [groceries, setGroceries] = useState('');
    // const [item, setItem] = useState('')
    // const [setGrocery, setHasGroceries] = useState(true);

    // function showList() {

    //     if (!showGroceries) {
    //         fetch('/api/groceries')
    //         .then((res) => {
    //             return res.json();
    //         })
    //         .then((data) => {
    //             setGroceries(data);
    //         });
    //     }

    //     setShowGroceries(!showGroceries);
    // }

    useEffect(() => {
        fetch('/api/groceries')
        .then((res) => {
            return res.json();
        })
        .then((data) => {
            console.log(data)
            setGroceries(data.item)
        })
    }, []);

    function inputCallback(event) {
        setGroceries(event.target.value);
    }

    return (
        <>
            <Header />
            <FormContainer groceries={groceries} setGroceries={setGroceries} inputCallback={inputCallback} / >
            <GroceryList groceries={groceries} />
        </>
    )
}

export default App;
